import { create } from 'ipfs-http-client';

// Infura API key and secret
const projectId = 'b0fb38809be7452e89656f0471c366e0';
const projectSecret = 'Vgwqs0HUkcWYo4gfXRD9jUkl5JZnbdRnrd+8I2PceeZOR5GgtJfsow';
const auth = 'Basic ' + Buffer.from(`${projectId}:${projectSecret}`).toString('base64');

const ipfs = create({
  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
    authorization: auth,
  },
});

export default ipfs;
