  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
    authorization: 'Basic ' + Buffer.from('b0fb38809be7452e89656f0471c366e0' + ':' + 'Vgwqs0HUkcWYo4gfXRD9jUkl5JZnbdRnrd+8I2PceeZOR5GgtJfsow').toString('base64')
  }
});

